import React, { useState } from 'react';
const AuthContext = React.createContext({
    isLoggedIn : false,
    onLogout : ()=>{},
    onLogin : ()=>{}
});
//It creates context object.

export const AuthContextProvider = (props)=>{
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const storedUserLoggedInInformation = localStorage.getItem("LoggedIn");
    
        if (storedUserLoggedInInformation === "1") {
          setIsLoggedIn(true);
        }
      }, []);
    const logoutHandler =()=>{
        localStorage.removeItem("LoggedIn");
        setIsLoggedIn(false);
    }
    const loginHandler = () =>{
        localStorage.setItem("LoggedIn", "1");
        setIsLoggedIn(true);
    }
    return(
        <AuthContext.Provider value={{isLoggedIn : isLoggedIn, onLogout : logoutHandler, onLogin : loginHandler}}>{props.children}</AuthContext.Provider>
    );
}
export default AuthContext;
